INSERT INTO `comments` (`id`, `comment`, `station_id`) VALUES
(1, 'Gospodarstwo', 588),
(2, '    Litwo! Ojczyzno moja! ty jesteś jak zdrowie:', 394),
(3, 'Ile cię trzeba cenić, ten tylko się dowie,', 29),
